mapper.select:
select  * from wd_mb_hyper_fol
where org_id  like CONCAT("%",#{orgId},"%")
and idno =  #{idNo}
and death_date is null
order by follow_up_date desc limit 0,1;

select * from wd_mb_hyper_fol where
org_id = #{orgId}
and orgin = #{orgin}
and idno = #{idNo}
order by follow_up_date desc limit 0,1;

select * from wd_mb_hyper_fol where
org_id = #{orgId}
and date_format(DATE_SUB(follow_up_date, INTERVAL 0 MONTH), '%m') = #{month}
and idno = #{idNo}
order by follow_up_date asc ;

select * from wd_mb_hyper_fol where
org_id like CONCAT("%",#{orgId},"%")
and idno = #{sfz,jdbcType=VARCHAR}
order by follow_up_date asc ;

select * from wd_mb_hyper_fol where
org_id like CONCAT("%",#{orgId},"%")
and idno = #{sfz,jdbcType=VARCHAR}
and (death_date is null or death_date = '')
and (lost_date is null or lost_date = '')
order by follow_up_date asc ;

select * from wd_mb_hyper_fol where
org_id like CONCAT("%",#{orgId},"%")
and idno = #{idNo,jdbcType=VARCHAR}
and follow_up_date = #{sfrq,jdbcType=VARCHAR} limit 0,1;

select * from wd_mb_hyper_fol where
org_id like CONCAT("%",#{orgId},"%")
and idno = #{idNo,jdbcType=VARCHAR}
and follow_up_date <#{sfrq,jdbcType=VARCHAR}
ORDER BY follow_up_date desc limit 0,1;

select * from wd_mb_hyper_fol where
org_id like CONCAT("%",#{orgId},"%")
and idno = #{idNo,jdbcType=VARCHAR}
ORDER BY follow_up_date desc limit 0,1;

select * from wd_mb_hyper_fol where
org_id like CONCAT("%",#{orgId},"%")
and idno = #{idNo,jdbcType=VARCHAR}
and follow_up_date > #{sfrq,jdbcType=VARCHAR}
ORDER BY follow_up_date asc limit 0,1;
